# Part 1
floor = input.count("(") - input.count(")")
puts "Santa ends on floor #{floor}."

# Part 2
floor = 0
santa_hits_basement = 0
input.length.times do |i|
  case input[i]
  when "("
    floor += 1
  when ")"
    floor -= 1
  end
  if floor == -1
    santa_hits_basement = i + 1
    break
  end
end

if santa_hits_basement != 0
  puts "Santa went through the basement at character #{santa_hits_basement}."
end